const Textarea =(props) =>{
    const [text, setText] = useState();
   
   return(
    <div>
        <h1>{props.heading}</h1>
        <div className="text-area">
            <textarea className='form-control' rows={8} columns></textarea>
        </div>
    </div>
    )
}

export default Textarea;
